import React from 'react';
import {Card, Image, Tag, Typography} from "antd";
import axios from "axios";

export default class PokeCard extends React.Component {
  state = {
    pokemon: {},
  };

  componentDidMount() {
    const {name} = this.props;

    axios.get('https://pokeapi.co/api/v2/pokemon/' + name, {
      'Accept': 'application/json',
      'Content-type': 'application/json'
    })
      .then((response) => {
        this.setState({pokemon: response.data})
      })
      .catch((error) => {
        console.log(error)
      });
  }

  _formatName = (name) => {
    if (!name) {
      return '';
    }

    const arr = name.split(' ');

    for (let i = 0; i < arr.length; i++) {
      arr[i] = arr[i].charAt(0).toUpperCase() + arr[i].slice(1);
    }

    return arr.join(' ');
  }

  _typeColor = (type) => {
    const pokeColors = {
      normal: '#A8A77A',
      fire: '#EE8130',
      water: '#6390F0',
      electric: '#F7D02C',
      grass: '#7AC74C',
      ice: '#96D9D6',
      fighting: '#C22E28',
      poison: '#A33EA1',
      ground: '#E2BF65',
      flying: '#A98FF3',
      psychic: '#F95587',
      bug: '#A6B91A',
      rock: '#B6A136',
      ghost: '#735797',
      dragon: '#6F35FC',
      dark: '#705746',
      steel: '#B7B7CE',
      fairy: '#D685AD',
    };

    return pokeColors[type] || '#777'
  }

  render() {
    const {pokemon} = this.state;

    if (!pokemon.name) {
      return null;
    }

    return (
      <Card title={this._formatName(pokemon.name)} bordered={false}>
        <div className="justify-content-center">
          <Image
            preview={false}
            width={200}
            height={200}
            src={'https://assets.pokemon.com/assets/cms2/img/pokedex/detail/' + pokemon.id.toString().padStart(3, '0') + '.png'}
            fallback="data:image/png;base64,"
          />
        </div>
        <Typography.Title level={4}>NÂº {pokemon.id}</Typography.Title>
        {
          pokemon.types.map((slot, index) => (
            <Tag key={slot.type.name} color={this._typeColor(slot.type.name)}>{this._formatName(slot.type.name)}</Tag>
          ))
        }
      </Card>
    );
  }
}